item.params:
params = JSON.parse(value);
req = new HttpRequest();

req.addHeader('Content-Type: application/json');
req.addHeader('x-api-key: ' + params.APIKey);
var query = 'query {';
query += '  array {';
query += '    disks {';
query += '      comment';
query += '      device';
query += '      format';
query += '      fsFree';
query += '      fsSize';
query += '      fsType';
query += '      fsUsed';
query += '      idx';
query += '      name';
query += '      rotational';
query += '      status';
query += '      temp';
query += '      transport';
query += '      type';
query += '    }';
query += '    capacity {';
query += '      kilobytes {';
query += '        free';
query += '        total';
query += '        used';
query += '      }';
query += '      disks {';
query += '        free';
query += '        total';
query += '        used';
query += '      }';
query += '    }';
query += '    state';
query += '  }';
query += '}';

const obj = {query: query, variables: {}};

resp = req.post('http://' + params.connect + '/graphql', JSON.stringify(obj));

return resp;

