parameters.parameter:
var obj = JSON.parse(value);
for (var i = 0; i < obj.data.disks.length; i++) {
    obj.data.disks[i]['idx'] = i;
    obj.data.disks[i]['device'] = obj.data.disks[i]['device'].replace('/dev/', '');
}
return JSON.stringify(obj);

